import {Injectable} from '@angular/core';
import {Product} from "../product";


@Injectable({
  providedIn: 'root'
})
export class CartService {
  constructor() { }

  products: Product[] = [];


  addToCart(product: Product) {
    this.products.push(product);
  }

  getItems() {
    return this.products;
  }

  clearCart() {
    this.products = [];
    return this.products;
    localStorage.setItem('key', products)
  }
}
